package incrementoDecremento;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//incremento ++
		//decremento --
		
		int numero = 5;

		// incremento variable ++; suma de a 1
		// decremento variable --; resta de a 1
		// se incrementa o se decrementa en funcion de 1
		numero++;
		
		System.out.println("Numero es ="+ numero);
	}

}
