package operadoresAritmeticos;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Operadores Aritmeticos
		// suma +
		// resta -
		// multiplicacion *
		// Division /
		// Modulo % nos ayuda a tener los residuos de todas las divisiones que hagamos
		// el residuo de 40 / 3 = 1
		
		int a, b;
		int resultado = 0;
		
		a = 5;
		b = 10;
		
		resultado = a + b;
		
		// creo la variable resultado porque la suma de a y b se hace dentro de una varible
		// aunque tambien podria a verlo definido arriba
		

		int nice = 0;
		
		nice = 40 % 3;
		

		System.out.println(resultado);
		System.out.println(nice);
	}

}
