	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//operadores logicos
		//< menor que
		//> mayor que
		// == igualdad
		// != diferente de
		// <= menor o igual
		// >= mayor o igual
		
		
		//los operadores devuelven 2 tipos de valor
		//true = verdadero
		//false = falso
		
		// 1 < 5 = true
		// 1 > 5 = false
		// 1 == 5 = false
		// 1 != 5 = true
		// 1 <= 5 = true
		// 1 >= 5 = false
		
		int var1 = 1;
		int var2 = 5;
		
		
		if(var1 < var2) {
			System.out.println("true");
		}else {
			System.out.println("False");
			
		}
	}

